mapper.update:
UPDATE MEMBER
SET PHONE = #{phone}
WHERE M_NO = #{mNo}

UPDATE MEMBER
SET PUSH_ENABLED =#{pushEnabled}
WHERE M_NO =#{mNo}

UPDATE DRIVER
SET ID_IMG_ORIGIN = #{idImgOrigin},
	ID_IMG_RENAME = #{idImgRename}
WHERE D_M_NO = #{dmNo}

mapper.select:
SELECT COUNT(*)
FROM ADDRESS
WHERE A_M_NO=#{mNo}

SELECT COUNT(*)
FROM RESERVATION R LEFT OUTER JOIN PAYMENT P ON(R.R_NO=P.P_R_NO)
LEFT OUTER JOIN CALCULATE C ON (P.P_NO = C.C_P_NO)
	
		
		WHERE R.R_M_NO = #{mNo}
		
		
		WHERE R.R_D_NO = #{dNo}
		
	
	 
		
			AND (P.PAY_YN = 'Y' AND P.CANC_YN ='N' AND R.DEAL_YN ='N')
		
		
			AND (P.PAY_YN = 'Y' AND P.CANC_YN ='N' AND R.DEAL_YN ='N')
		
		
			AND (P.DEAL_YN = 'Y' AND P.CANC_YN ='N')
		
	
		 AND R.START_DATE BETWEEN #{stDate} AND #{edDate} 
	
		 	AND ((R.START_ADDR LIKE '%'||#{fSearch}||'%') OR (R.END_ADDR LIKE '%'||#{fSearch}||'%') OR (R.MSG LIKE '%'||#{fSearch}||'%') OR(R.LUGGAGE LIKE '%'||#{fSearch}||'%'))

SELECT * FROM RESERVATION R LEFT OUTER JOIN PAYMENT P ON(R.R_NO=P.P_R_NO) LEFT OUTER JOIN CALCULATE C ON (P.P_NO = C.C_P_NO)
WHERE R.R_M_NO = #{mNo}
WHERE R.R_D_NO = #{dNo}
AND (P.PAY_YN = 'Y' AND P.CANC_YN ='N' AND R.DEAL_YN ='N')
AND (P.PAY_YN = 'Y' AND P.CANC_YN ='N' AND R.DEAL_YN ='N')
AND (P.DEAL_YN = 'Y' AND P.CANC_YN ='N')
AND R.START_DATE BETWEEN #{stDate} AND #{edDate}
AND ((R.START_ADDR LIKE '%'||#{fSearch}||'%') OR (R.END_ADDR LIKE '%'||#{fSearch}||'%') OR (R.MSG LIKE '%'||#{fSearch}||'%') OR(R.LUGGAGE LIKE '%'||#{fSearch}||'%'))
ORDER BY TO_NUMBER(SUBSTR(R.R_NO, 2)) DESC

SELECT M.NAME REGCARD_IMG_ORIGIN, M.PHONE REGCARD_IMG_RENAME, D.CAPACITY, D.TYPE, D.CAR_NO, D.ID_IMG_RENAME
FROM MEMBER M LEFT OUTER JOIN DRIVER D ON(M.M_NO=D.D_M_NO)
WHERE D.D_NO=#{dNo}

SELECT *
FROM RESERVATION R LEFT OUTER JOIN PAYMENT P ON(R.R_NO=P.P_R_NO)
WHERE R_NO = #{rNo}

mapper.update:
UPDATE RESERVATION
SET MSG = #{msg}
WHERE R_NO = #{rNo}

mapper.select:
SELECT COUNT(*)
FROM RESERVATION R LEFT OUTER JOIN PAYMENT P ON(R.R_NO=P.P_R_NO)
WHERE R.R_D_NO= #{DNO}

SELECT COUNT(*) FROM RESERVATION R LEFT OUTER JOIN PAYMENT P ON(R.R_NO=P.P_R_NO) LEFT OUTER JOIN CALCULATE C ON (P.P_NO = C.C_P_NO)
WHERE R.R_M_NO = #{mNo}
WHERE R.R_D_NO = #{dNo}
AND (R.START_DATE > #{stDate}}) AND (R.START_DATE < #{edDate})
AND ((R.START_ADDR LIKE '%'||#{fSearch}||'%') OR (R.END_ADDR LIKE '%'||#{fSearch}||'%') OR (R.MSG LIKE '%'||#{fSearch}||'%') OR(R.LUGGAGE LIKE '%'||#{fSearch}||'%'))

SELECT * FROM RESERVATION R LEFT OUTER JOIN PAYMENT P ON(R.R_NO=P.P_R_NO) LEFT OUTER JOIN CALCULATE C ON (P.P_NO = C.C_P_NO)
WHERE R.R_M_NO = #{mNo}
WHERE R.R_D_NO = #{dNo}
AND (R.START_DATE > #{stDate}}) AND (R.START_DATE < #{edDate})
AND ((R.START_ADDR LIKE '%'||#{fSearch}||'%') OR (R.END_ADDR LIKE '%'||#{fSearch}||'%') OR (R.MSG LIKE '%'||#{fSearch}||'%') OR(R.LUGGAGE LIKE '%'||#{fSearch}||'%'))
ORDER BY TO_NUMBER(SUBSTR(R.R_NO, 2)) DESC
